import React, {Component} from 'react';

import Navbar from 'react-bootstrap/Navbar'

// import ArtTrackIcon from '@material-ui/icons/ArtTrack'
// import FeaturedVideoIcon from '@material-ui/icons/FeaturedVideo'
// import {FormControl, Input, Button, List, ListItem, ListItemText, ListItemIcon, ListItemSecondaryAction} from '@material-ui/core'

class FilteredList extends Component {
    constructor() {
        super()
        this.state = {
            year : "All"
        }
    }

    onSelectFilterYear = event => {
        this.setState({
            year: event
        })
    }

    matchesFilterSize = item => {
        if (this.state.year === "All") {
            return true
        } else if (this.state.year === item.year) {
            return true
        } else {
            return false
        }
    }

    render() {
        // const todoEntries = this.props.entries
        // const listItems = todoEntries.map(this.createTask)
        return (
            <Navbar bg="light" expand="lg">
                <Navbar.Brand href="#home">Your T swizzle Playlist</Navbar.Brand>
                <Navbar.Toggle aria-controls="basic-navbar-nav" />
                <Navbar.Collapse id="basic-navbar-nav">
                <Nav className="mr-auto">

                    <Nav.Link href="#home">Home</Nav.Link>
                    <Nav.Link href="#link">Link</Nav.Link>

                    <NavDropdown title="Album" id="basic-nav-dropdown">
                        {/* <NavDropdown.Item eventKey="All">Taylor Swift</NavDropdown.Item>
                        <NavDropdown.Item href="#action/3.2">Red</NavDropdown.Item>
                        <NavDropdown.Item href="#action/3.3">1989</NavDropdown.Item>
                        <NavDropdown.Item href="#action/3.3">Lover</NavDropdown.Item>
                        <NavDropdown.Item href="#action/3.3">Folklore</NavDropdown.Item> */}

                    </NavDropdown>

                    <NavDropdown title="Sort by Year" id="basic-nav-dropdown">
                        <NavDropdown.Item eventKey="All" onSelect={this.onSelectFilterYear}>Select</NavDropdown.Item>
                        <NavDropdown.Item eventKey="asc" onSelect={this.onSelectFilterYear}>Older to Newer</NavDropdown.Item>
                        <NavDropdown.Item eventKey="dsc" onSelect={this.onSelectFilterYear}>Newer to Older</NavDropdown.Item>
                    </NavDropdown>

                </Nav>
                </Navbar.Collapse>
            </Navbar>

        )
    }
}

export default FilteredList
