import React, {Component} from 'react';
import { ListGroupItem } from 'react-bootstrap';

import ListGroup from 'react-bootstrap/ListGroup'

class Playlist extends Component {


    createTask = item => {
        return (
            <ListGroup.Item>
                <ListGroupItem>
                {item.name} <br></br>
                Duration: {item.length}
                </ListGroupItem>


                {/* </ListItemText> */}
            </ListGroup.Item>
        )
    }

    calculate_time = item => {
        const time_list = item.length.split(".")
        this.total_minutes += parseInt(time_list[0])
        this.total_seconds += parseInt(time_list[1])
        if (total_seconds > 59) {
            total_seconds = total_seconds - 60
            total_minutes += 1
        }
        return (
            <h6>
                Total Time: {this.total_minutes} . {this.total_seconds}
            </h6>
        )
    }
    render() {
        const items = this.props.entries
        const listItems = items.map(this.createTask)
        const timeDuration = items.map(this.calculate_time)
        const total_minutes = 0
        const total_seconds = 0
        return (
            <ListGroup>
                <h4>
                    Your Playlist:
                </h4>
                {listItems}
                {timeDuration}

            </ListGroup>
        )
    }

}

export default Playlist
